import React, { FC, useState } from 'react'
import { useAppDispatch } from '../hook'
import { boredReducerFetch } from './BoredReducer'

export const Form:FC = () => {
    const [type, setType] = useState<string>('social');
    const [participants, setParticipants] = useState<number>(0);
    const dispatch = useAppDispatch()

    const options = [
        {label: 'Social', value: 1},
        {label: 'Relaxation', value: 2},
        {label: 'Music', value: 3},
        {label: 'Education (max: 1)', value: 2},
        {label: 'DIY', value: 6},
        {label: 'Cooking', value: 7},
        {label: 'Busywork', value: 8},
        {label: 'Charity', value: 9},
    ]

    const handelParticipantsChange:React.ChangeEventHandler<HTMLInputElement> = (e) => {
        if(e.target.value){
            setParticipants(parseInt(e.target.value))
        }else{
            setParticipants(0)
        }
    }

    const handleClick:React.MouseEventHandler<HTMLButtonElement> = () => {
        dispatch(boredReducerFetch([type.toLowerCase(), participants]))
    }

    const handleSubmit:React.FormEventHandler<HTMLFormElement> = e => {
        e.preventDefault();
    }
    
    const handleSelect:React.ChangeEventHandler<HTMLSelectElement> = (e) => {
        const selectedItem = e.target.value
        options.forEach(el => {
            if(el.value === parseInt(selectedItem)) setType(el.label)
        })
    }
  
  return (
    <form onSubmit={handleSubmit}>
        <label htmlFor="select"></label>
        <select id="select" onChange={handleSelect}>
            {options.map(option => 
                <option key={option.value} value={option.value}>{option.label}</option>    
            )}
        </select>
        <input min={0} type="number" onChange={handelParticipantsChange} placeholder='Participants...' />
        <button type='button' onClick={handleClick}>Find</button>
    </form>
  )
}
