import React, { FC, useState } from 'react'
import { useAppDispatch } from '../hook'
import { boredReducerFetch } from './BoredReducer'

export const Form:FC = () => {
    const [type, setType] = useState<string>('');
    const [participants, setParticipants] = useState<number>(0);
    const dispatch = useAppDispatch()
    const handleClick:React.MouseEventHandler<HTMLButtonElement> = () => {
        dispatch(boredReducerFetch([type, participants || 0]))
    }

    const handleSubmit:React.FormEventHandler<HTMLFormElement> = e => {
        e.preventDefault();
    }
    
  
  return (
    <form onSubmit={handleSubmit}>
        <label htmlFor="select"></label>
        <select name="" id="select">
            <optgroup>Choose activity...</optgroup>
            <option value=""></option>
        </select>
        <input type="number" onChange={e => setParticipants(parseInt(e.target.value))} placeholder='Participants...' />
        <button type='button' onClick={handleClick}>Find</button>
    </form>
  )
}
