import React, { FC, useEffect, useRef } from 'react'
import { useAppSelector } from '../hook'

interface IImages {
  busywork: string;
  cooking: string;
}

export const Activity:FC = () => {
    const boreds = useAppSelector(state => state.card.card[0])
    const images:IImages = {
      busywork: '../img/busywork.jpg',
      cooking: '../img/cooking.jpg',
    }
    const imageRef = ''

    useEffect(() => {
      switch (boreds.type) { 
        case 'cooking': imageRef.current = images.cooking
          break;
      }
    }, [boreds])
  return (

    <div>
       {boreds ? 
        <div key={boreds.key}>
            <h2>{boreds.activity}</h2>
            <p>
                Type: {boreds.type}<br />
                Participants: {boreds.participants} <br />
                Price: {boreds.price}
            </p>
            
            <img src={imageRef} alt="" />
        </div>

       : null}
    </div>
  )
}
