import React, { FC, useEffect, useState } from 'react'
import { useAppDispatch, useAppSelector } from '../hook'
import { boredReducerFetch } from './BoredReducer'

export const Form:FC = () => {
    const [type, setType] = useState<string>('');
    const [participants, setParticipants] = useState<number>(0);
    const boreds = useAppSelector(state => state.card.card)
    const dispatch = useAppDispatch()

    const handleClick:React.MouseEventHandler<HTMLButtonElement> = () => {
        dispatch(boredReducerFetch([type]))
    }

    const handleSubmit:React.FormEventHandler<HTMLFormElement> = e => {
        e.preventDefault();
    }
    
  
  return (
    <form onSubmit={handleSubmit}>
        <input type="text" onChange={e => setType(e.target.value)} placeholder='Type...'/>
        <input type="number" placeholder='Participants...' />
        <input type="number" placeholder='Price...'/>
        <button type='button' onClick={handleClick}>Find</button>
    </form>
  )
}
