  const load = useAppSelector(state => state.card.error)

  return (
    <div className='wrapper'>
      {error ? 
        <div className="lds-hourglass"></div>
        :null
      }
      <h1>Are you bored?</h1>
      <Form />
      <Activity />
    </div>
  )
}
